	cout << maxx.num << " " << maxx.ts << endl;//输出 

	peo[maxx.num].cr = peo[maxx.num].er = peo[maxx.num].mr = peo[maxx.num].num = peo[maxx.num].ts = 0;//找到了置0 

	maxx.num = maxx.cr = maxx.ts = 0;//清0 
}
