void gb(int l,int r)//归并排序
{
    if(l==r)return;
    int mid,i,j,k=l;
    mid=(l+r)/2;
    gb(l,mid);
    gb(mid+1,r);
    i=l;j=mid+1;
    while(i<=mid&&j<=r)
    {
