        if(e[i].s>e[j].s)t[k++]=e[i++];
        else if(e[i].s<e[j].s)t[k++]=e[j++];
        else//如果总分相等，则比较语文分数
        {
            if(e[i].a>e[j].a)t[k++]=e[i++];
