int main()
{
	int y[1000]={0},sum[1000]={0},num[1000]={0},n;//语文、总分、序号
	cin>>n;
	for(int i=1;i<=n;i++)
	{
		int x,z;//因为数学和英语成绩不需要单独加入排序，所以不用用数组
		cin>>y[i]>>x>>z;//输入
		sum[i]=x+y[i]+z;//累计总分
		num[i]=i;//记录序号
	}
