    if(x.all>y.all) return 1;//如果总分可以比较
    if(x.all==y.all)//反之
    {
        if(x.a>y.a)//如果语文分数可以比较
        return 1;
        if(x.a==y.a)//反之
        {
            if(x.num<y.num)//比较学号
            return 1;
        }
    }
    return 0; 
}
int main()
{
    int n;
    cin>>n;//输入
    for(int i=1;i<=n;i++)
    {
        cin>>g[i].a>>g[i].b>>g[i].c;
        g[i].num=i;//记录学号
        g[i].all=g[i].a+g[i].b+g[i].c;//记录总分
    }
    sort(g+1,g+n+1,cmp);//三级排序
    for(int i=1;i<=5;i++)
        cout<<g[i].num<<' '<<g[i].all<<endl;//输出
}
