```cpp
#include <cstdlib>
#include <iostream>
using namespace std;
int main(int argc, char *argv[])
{
    int n,a[1001],b[1001],c[1001],x[1001],s[1001];//学生的三个成绩，编号，和
    int i,j;
    cin>>n;
    for(i=1;i<=n;i++)
    {
     x[i]=i;//序号
     cin>>a[i]>>b[i]>>c[i];
     s[i]=a[i]+b[i]+c[i]; //和              
    } 
    for(i=1;i<n;i++)
    {
     for(j=i+1;j<=n;j++)
     {
      if(s[i]<s[j])//最弱的排序。当然你可以改成别的。
      {
      swap(s[i],s[j]);//替换
      swap(a[i],a[j]);//替换
      swap(x[i],x[j]);//替换
      }
      if(s[i]==s[j])
      {
       if(a[j]>a[i])//第一项
       {
       swap(s[i],s[j]);
       swap(a[i],a[j]);
       swap(x[i],x[j]);
       }
       if(a[j]==a[i])
        if(x[j]<x[i])//继续替换
        {
         swap(s[i],s[j]);
         swap(a[i],a[j]);
         swap(x[i],x[j]);
        }            
      }                
     }                
    }
    for(i=1;i<=5;i++)//输出
    cout<<x[i]<<" "<<s[i]<<endl;
    return EXIT_SUCCESS;
}
