int w,n,p[30001],ans=0;
int* pHead,* pTail;
int main(){
	cin>>w>>n;
	for(int i=0;i<n;i++){
		cin>>p[i];
	}
	pHead=&p[0];
	pTail=&p[n];
	sort(pHead,pTail);
	pTail--;
	while(pHead<pTail){
		if(*pHead+*pTail>w){
			pTail--;
		}else{
			pHead++;
			pTail--;
		}
		ans++;
	}
	if(pHead==pTail){
		ans++;
	}
	cout<<ans<<endl;
	return 0;
}

