#include<iostream>
#include<algorithm>
using namespace std;
int n,W,a[66666],sum;
bool p[66666];

void quick_sort(int s[], int l, int r)
{
    if (l < r)
    {
        int i = l, j = r, x = s[l];
        while (i < j)
        {
            while(i < j && s[j] >= x) j--;  
            if(i < j) s[i++] = s[j];
            while(i < j && s[i] < x) i++;  
            if(i < j) 
				s[j--] = s[i];
        }
        s[i] = x;
        quick_sort(s, l, i - 1); 
        quick_sort(s, i + 1, r);
    }
}

int main()
{
	cin>>W>>n;
	for(int i=0; i<n; i++) cin>>a[i];
	quick_sort(a,0,n-1);
	int l=0,r=n-1;
	while(l <= r)
	{
		if(a[l] + a[r] <= W &&!p[l]&&!p[r]) 
		{
		    p[l] = true;p[r] = true;
			sum++;l++,r--;
		}
		else r--;
	}
	for(int i=0; i<n; i++) if(!p[i]) sum++;
	cout<<sum;
}
