using namespace std;
const int N = 30005;
int a[N];
int main() {
    int w, n, ans = 0;
    scanf("%d%d", &w, &n);
    for(int i = 1; i <= n; i++)
        scanf("%d", &a[i]);
    sort(a+1, a+1+n); //升序排序
    int l = 1, r = n; //l:头变量，r:尾变量
    while(l < r) //循环条件 每次循环ans++
        if(a[l] + a[r] <= w) //如果头尾相加没有超过限制
            ans++, l++, r--; //则头尾变量皆向中间移动
        else
            ans++, r--; //超出限制，则只有尾变量向前移动
    if(l == r) ans++; //给在中间的单独的数据分一组
    printf("%d", ans);
    return 0;
}

