			if(i+j<=w&&i!=j)
			{
				int t=min(a[i],a[j]);
				a[i]-=t;
				a[j]-=t;
				ans+=t;
			}
			else if(i+j<=w)//重点！！两个相同的数！
            {
				if(a[i]%2==0)
				{
					ans+=a[i]/2;
				}
				else
