					ans+=a[i]/2+1;
				}
				a[i]=0;
			}
		}
	}
	for(i=5;i<=200;i++)
	{
		if(a[i]!=0)
		{
			ans+=a[i];//一个数单独一组的情况
		}
	}
	cout<<ans;
}
