        read(num);
        inc(buck[num]);
    end;
    x:=0;
    for i:=w downto 5 do //从w开始就已经足够了
    begin
        t:=buck[i];
        if t = 0 then
            continue;
        for j:=1 to t do
        begin
            a[x + 1]:=i;
            inc(x);
        end;
    end;
    //以上这大段就是传说中的“桶排序”，O(n)的复杂度，可以把时间省到极限，尽管快排的n log n并没有超出10^6
    group:=0;
    left:=1;
    right:=n;
    while left < right do //注意不是<=
    begin
        if (a[left] + a[right] > w) then //该条件已经隐含了(a[left] >= w)
        begin
            inc(left);
            inc(group); // a[left]独立成为一组
            continue;
        end;
        inc(group);//a[left] 与 a[right] 共为一组
        inc(left);
        dec(right);
    end;
    if left = right then
        inc(group); //这一组即是唯一那个孤零零处在当中的数
    //若left>right,则中间并未夹有多余的数，无需无中生有地添一组
    write(group);
end.
```

一道简单的贪心。。。

利用5..200的数组来统计价格为Pi的纪念品个数，i从200循环到5，j从w-i循环到5，统计能凑成一组的纪念品，余下的单独加。

注意如果i的值小于等于w div 2，需要特判避免漏算。

不多说，还是看代码吧。

```cpp
program p1094;
  var a:array[5..200]of longint;//该数组统计不同价值的纪念品个数。
      i,j,n,w,x,ans,t:longint;
function min(a,b:longint):longint;
begin
  if a<b then exit(a) else exit(b);
end;
begin
  readln(w);
  readln(n);
  for i:=1 to n do
  begin
    readln(x);
    inc(a[x]);
  end;
  for i:=200 downto 5 do
  begin
    for j:=(w-i) downto 5 do
    if (a[i]>0) and (a[j]>0) and (i<>j) then//将能凑在一起的纪念品凑在一起。
    begin
      t:=min(a[i],a[j]);
      dec(a[i],t);
      dec(a[j],t);
      inc(ans,t);
    end
    else if (i=j) and (i+j<=w) then//特判。
    begin
      ans:=ans+a[i] div 2;
      a[i]:=a[i] mod 2;
    end;
    ans:=ans+a[i];//剩下没能配对的一个纪念品一组。
    a[i]:=0;
  end;
  writeln(ans);//输出纪念品组数。
end.
```

