var zh,i,n,z,j:longint;
    a:array[0..30000] of longint;
procedure qsort(l,r:longint);
var i,j,mid,by:longint;
begin
  i:=l;
  j:=r;
  mid:=a[(i+j) div 2];
  repeat
    while a[i]<mid do inc(i);
    while mid<a[j] do dec(j);
    if i<=j then
      begin
        by:=a[i];a[i]:=a[j];a[j]:=by;
        inc(i);
        dec(j);
