void Record()
{
    int carry_bit=0;
    for(int i=1;i<=len;i++){
        num[i]=num[i]*2;
        num[i]+=carry_bit;
        if(num[i]<10){
            carry_bit=0;
        }
        else{
            carry_bit=num[i]/10;
            num[i]=num[i]%10;
            if(i==len){
