	register int i;
	n=read();
	a[1]=1;
	for(i=1;i<=n+1;i++)//2的n次方
	f1(2);
	f2();//高精度减法
	for(i=l;i>=1;i--)printf("%lld",a[i]);
	printf("\n");//最好换行
	return 0;
}
