```cpp

#include<iostream>
using namespace std;
struct tree{
    int value,times;
    tree *tc,*rc;
}*root;
int a,n;
void insertx(tree* &root,int x)
{
    if(root!=NULL)
    {
        if(x<root->value)
            insertx(root->tc,x);
        else if(x>root->value)
            insertx(root->rc,x);
        else root->times++;
    }
    else{
        root=new tree;
        root->value=x;
        root->times=1;
        root->tc=root->rc=NULL;
    }
}
void inorder(tree *root)//中根遍历
{
    if(root!=NULL)
    {
        inorder(root->tc);
        cout<<root->value<<" "<<root->times<<endl;
        inorder(root->rc);
    }
}
int main()
{
    cin>>n;
    for(int i=1;i<=n;i++)
    {
        cin>>a;
        insertx(root,a);
    }
    inorder(root);
    return 0;
}

