	for (int i=0;i<n;i++)//输入操作
	{
		int t;
		cin>>t;
		s.insert(t);
	}
	for (set<int>::iterator it=s.begin();it!=s.end();it++)//遍历集合
	{
		if (it==s.begin())//特判开头，处理初值
		{
			now=*it;
			nans=1;
		}
		else if (*it==now)nans++;//如果还没到下一个数，当前统计的数量就++
		else//如果变了就输出上一个数和它的数量，并更改当前数，还原计数
		{
			cout<<now<<' '<<nans<<endl;
			now=*it;
			nans=1;
		}
	}
	cout<<now<<' '<<nans;//最后一个数的输出操作
   return;
}
