           //与上同理,逆序插入
           if(p1==1&&((x>=97&&y<=122)||(x>=48&&y<=57)))
					for(i=1;i<=y-x-1;i++)
						a.insert(k,p2,char(x+i));
				if(p1==2&&((x>=97&&y<=122)))
					for(i=1;i<=y-x-1;i++)
						a.insert(k,p2,char(x+i-32));
				if(p1==2&&(x>=48&&y<=57))
