```cpp
#include<iostream>
using namespace std;
int p1,p2,p3;
string a;
int judge(int x)
{
    if(a[x+1]<=a[x-1])//不合法情况：右边的ASCII小于等于左边的ASCII 
    return 0;
    if(a[x-1]>='0'&&a[x-1]<='9'&&a[x+1]>='0'&&a[x+1]<='9')
    return 1;//合法情况：数字 
    if(a[x-1]>='a'&&a[x-1]<='z'&&a[x+1]>='a'&&a[x+1]<='z')
    return 2;//合法情况：字母 
    return 0;//不合法情况：前后为不同类型的字符 
}
string produce(int g,int s,int t,int x,int y,int z)
{
    string tmp;
    tmp.clear();
    if(x==3)//*号 
    {
        tmp.append((a[t]-a[s]-1)*y,'*');
        return tmp;
    }
    if(g==1)//数字 
    {
        for(int i=1;i<=a[t]-a[s]-1;i++)
        {
            tmp.append(y,a[s]+i);
        }
    }
    if(g==2)//处理字母 
    {
        if(x==1)//小写 
        {
            for(int i=1;i<=a[t]-a[s]-1;i++)
            {
                tmp.append(y,a[s]+i);
            }
        }
        else if(x==2)//大写 
        {
            for(int i=1;i<=a[t]-a[s]-1;i++)
            {
                tmp.append(y,a[s]+i-32);
            }
        }
    }
    if(z==2)//p3==2，反转 
    {
        string b=tmp;
        int l=tmp.size();
        for(int i=0;i<l;i++)
        b[l-i-1]=tmp[i];
        return b;//返回生成的字符串 
    }
    return tmp;//返回生成的字符串 
}
int main()
{
    cin>>p1>>p2>>p3;
    cin>>a;
    int flag=0;
    while(1)
    {
        int f=a.find('-',flag);
        if(f==-1) break;
        int g=judge(f);
        if(g!=0)//返回1为数字，2为小写，0为不合法 
        {
            if(a[f-1]+1==a[f+1])//右边的字符恰好是左边字符的后继
            {
                a.erase(f,1);//仅删除减号 
                continue;
            }
            string t=produce(g,f-1,f+1,p1,p2,p3);//生成中间的字符串 
            a.replace(f,1,t);//用生成的替换掉减号 
        }
        flag=f+1;
    }
    cout<<a;//输出修改完成的字符串 
    return 0;
}
