	for(int i = 1;i <= len; i++){
		if(a[i] == '-' ){//如果遇到了减号 
			if(judge1(a[i - 1],a[i + 1])){//看它两边是不是符合要求的 
				x = (int)a[i - 1];//用int来进行辨别更加方便 
				y = (int)a[i + 1];
