inline void reverse(int l,int r){
	if(p3==1){
		for(int i=l;i<=r;i++)
		    for(int j=1;j<=p2;j++)
				printf("%c",deal(char(i)));//deal函数处理大小字母的情况
	}
	if(p3==2){
		for(int i=r;i>=l;i--)
			for(int j=1;j<=p2;j++)
				printf("%c",deal(char(i)));//deal函数处理大小字母的情况
	}
}
