```cpp
#include <ctype.h>
#include <stdbool.h>
#include <stdio.h>
#include <string.h>
#define MAXN 300
char input[MAXN], result[MAXN << 10]; // 我数组开爆
bool used[MAXN];
int p1, p2, p3, ri = 0;
// 主要用于处理p1
char mkchar(char c) {
    if (p1 == 1) return tolower(c);
    else if (p1 == 2) return toupper(c);
    else return '*';
}
// 防止重复输出，抽个函数出来简化代码
void send(int ind) {
    if (!used[ind]) {
        result[ri++] = input[ind];
        used[ind] = true;
    }
}
// 检查需不需要展开
bool check(int ind) {
	// 首末减号不展，首先判断防止RE
    if (ind == 0 || input[ind + 1] == '\0')
        return false;
    char l = input[ind - 1], r = input[ind + 1];
    // 逻辑是这样的：
    // 两边不都是字母， 两边不都是数字，这两个命题只有一个成立时即不展开
    // 同时成立的情况类似a--
    if ( (isalpha(l) ^ isalpha(r)) ^ ((isdigit(l) ^ isdigit(r)) )
        return false;
    // 如果b-a之类的就不展开
    if (l >= r)
        return false;
    return true;
}
// 正常的填充
void normal_fill(int ind) {
    send(ind - 1);
    char l = input[ind - 1], r = input[ind + 1];
    for (char c = l + 1; c < r; c++) {
        char p = mkchar(c); // 获得本次填充的字符
        for (int i = 0; i < p2; i++) // 处理p2
            result[ri++] = p;
    }
    used[ind] = true;
    send(ind + 1);
}
// 逆序的填充
void back_fill(int ind) {
    send(ind - 1);
    char l = input[ind - 1], r = input[ind + 1];
    for (char c = r - 1; c > l; c--) {
        char p = mkchar(c);
        for (int i = 0; i < p2; i++)
            result[ri++] = p;
    }
    used[ind] = true;
    send(ind + 1);
}
// 展开的主要进程
void tran() {
    for (int i = 0; input[i] != '\0'; i++) {
    	// 检测到-且合法就进入处理
        if (input[i] == '-' && check(i)) {
            char l = input[i - 1], r = input[i + 1];
            if (p3 == 1) // 处理p3
                normal_fill(i);
            else if (p3 == 2)
                back_fill(i);
        } else send(i); // 其他字母直接挪过去
        // 使用send封装可以不用考虑很多条件
    }
}
int main(void) {
    scanf("%d %d %d %s", &p1, &p2, &p3, input);
    tran();
    result[ri++] = '\0'; // 使之成为合法的C字符串
    printf("%s\n", result);
    return 0;
}

