```
#include <stdio.h>
#include <ctype.h>

int main(void)
{
	int p1, p2, p3;
	scanf("%d %d %d", &p1, &p2, &p3);
	char line[101] = {};
	scanf("%s", line);

	int i, j, k;
	for(i = 0; line[i] != 0; i++){
		if(line[i+1] == '-' && line[i] != '-' &&
        (bool)isalpha(line[i]) == (bool)isalpha(line[i+2])){
        /* 1. 测试此时line[i]是不是'-'前的字符。 
         * 2. 测试该字符是否为'-'，避免连续出现'-'时出错。
         * 3. 测试字符类型是否相等。可以检测是否同为数字或同为字母，
         *    此处检测是否同为字母。检测函数返回int型，在字符为字母
         *    时返回非0值（真）， 否则返回0（假）。使用bool类型以进
         *    行真假的比较，（否则1和2虽然都表示真，但不相等），因
         *    此需要使用C＋＋进行编译和提交。
         */
			putchar(line[i]);
            //由于是if-else if关系，不会走下面的输出路径，
            //所以在此输出
			if(line[i] >= line[i+2])
            //当'-'前字符大于等于'-'后字符时，输出'-'
				putchar('-');
			else {
				if(p3 == 1)
                //正序
					for(j = line[i]+1; j <= line[i+2]-1; j++)
						for(k = 0; k < p2; k++)
							if(p1 == 1)
                            //输出小写字母
								putchar(j);
							else if(p1 == 2 && isalpha(line[i]))
                            //由于题目中的字母全是小写，直接转换
								putchar(j+('A'-'a'));
							else if(p1 == 3)
								putchar('*');
							else 
                            //输出数字
								putchar(j);
				if(p3 == 2)
                //逆序
					for(j = line[i+2]-1; j >= line[i]+1; j--)
                    //同上，不再解释
						for(k = 0; k < p2; k++)
							if(p1 == 1)
								putchar(j);
							else if(p1 == 2 && isalpha(line[i]))
								putchar(j+('A'-'a'));
							else if(p1 == 3)
								putchar('*');
							else
								putchar(j);
			}
		}else if(line[i] != '-' || i == 0 || line[i-1] == '-' || (bool)isalpha(line[i-1]) != (bool）isalpha(line[i+1]))
        /* 1. 当line[i]为字符串中的其他字符 or '-'后的数字或字母时
         * 2. 当这是第一个字符时（杜绝开头'-'出错）
         * 3. 当上一个字符是'-'时（避免连续'-'出错）
         * 4. 当上一个字符与下一个字符不同类时（同上，不再赘述）
         */
			putchar(line[i]);
	}
	putchar('\n');
	return 0;
}
