    for(i=0;i<16;++i) s[i]=p[i+16];//17-32位交换
    for(i=16;i<32;++i) s[i]=p[i-16];//1-16位交换
    for(i=31;i>=0;--i){//将结果转为十进制
        ans+=s[i]*fact;
        fact*=2;
    }
    printf("%lld",ans);//输出
    return 0;//结束
}
