		}
	}
	for(int i=1;i<=n;i++)
	{
		for(int j=1;j<=n;j++)
		{
			if(Map[i][j]=='y')//找'y'; 
			{
				sx=i;sy=j;
				dfs(sx,sy);
			}
		}
	}
	for(int i=1;i<=n;i++)
	{
		for(int j=1;j<=n;j++)//直接判断vis的值为true还是false然后直接输出 
		{
			if(vis[i][j])//因为前面遍历全图将需要输出的字母赋值为true; 
			cout<<Map[i][j];
			else cout<<"*";//不成立的字母我们赋值为false 
		}
		cout<<endl;
	}
	return 0;
}
