		else if ( c-1>=0 && xl[r][c-1] == ch[ysc+1] && bl[r][c-1] == '*' ){
			bl[r][c-1] = ch[ysc+1];
			bl[r][c] = ch[ysc];
			dfs2(ysc+1,r,c-1);
			bl[r][c+1] = bl[r][c-1] = bl[r][c] = '*';
		}

	}
}

inline void dfs1 ( int ysc , int r , int c ){
	flag = false;
	if ( ysc == 6 ){
		int k = 0;
		for ( register int i = 0 ; i < n ; i++ ){
			if ( k == 7 ) {
				flag = true;
				break;
			}
			for ( register int j = 0 ; j < n ; j++ ){
				if ( bl[i][j] != '*' ){
					k++;
					ans[i][j] = bl[i][j];
				}
			}
		}
		return;
	}
	else {

		if ( r+1<=n && xl[r+1][c] == ch[ysc+1] && bl[r+1][c] == '*' ){
			bl[r+1][c] = ch[ysc+1];
			bl[r][c] = ch[ysc];
			dfs1(ysc+1,r+1,c);
			bl[r-1][c] = bl[r+1][c] = bl[r][c] = '*';
		}
		else if ( r-1>=0 && xl[r-1][c] == ch[ysc+1] && bl[r-1][c] == '*' ){
			bl[r-1][c] = ch[ysc+1];
			bl[r][c] = ch[ysc];
			dfs1(ysc+1,r-1,c);
			bl[r+1][c] = bl[r-1][c] = bl[r][c] = '*';
		}

	}
}

int main() {

	cin >> n;

	for ( register int i = 0 ; i < n ; i++ ){
		cin >> xl[i];
	}

	for ( register int i = 0 ; i < 107 ; i++ ){
		for ( register int j = 0 ; j < 107 ; j++ ){
			bl[i][j] = '*';
			ans[i][j] = '*';
		}
	}

	for ( register int i = 0 ; i < n ; i++ ){
		for ( register int j = 0 ; j < n ; j++ ){
			if ( xl[i][j] == 'y' ){
				dfs1(0,i,j);//横
				if ( flag == true ) continue;
				dfs2(0,i,j);//纵
				if ( flag == true ) continue;
				dfs3(0,i,j);//左上
				if ( flag == true ) continue;
				dfs4(0,i,j);//左下
				if ( flag == true ) continue;
				dfs5(0,i,j);//右上 
				if ( flag == true ) continue;
				dfs6(0,i,j);//右下 
			}
		}
	}

	for ( register int i = 0 ; i < n ; i++ ){
		for ( register int j = 0 ; j < n ; j++ ){
			cout << ans[i][j];
		}
		cout << endl;
	}

	return 0;
}
