        for(j=1;j<=n;j++)//(j,i)为坐标
        {
            //i代表纵坐标，j代表横坐标 
            if(A[i][j]!='y' || ans[i][j]!='*')continue; 
            find=0;//每个起始点'y'最多找1个单词 
            //往8个方向搜索，剪枝(其实完全没必要剪) 
            if(i>=7)            dfs(2,j,i,0,-1);//上
            if(i<=n-6)            dfs(2,j,i,0,1);//下 
