            if(j<=n-6)            dfs(2,j,i,1,0);//右
            if(j<=n-6 && i>=7)    dfs(2,j,i,1,-1);//右上
            if(j>=7 && i>=7)    dfs(2,j,i,-1,-1);//左上
            if(j>=7 && i<=n-6)    dfs(2,j,i,-1,1);//左下
            if(j<=n-6 && i<=n-6)dfs(2,j,i,1,1);//右下 
            if(find) ans[i][j]='y';
        }
    for(i=1;i<=n;i++)
    {
        for(j=1;j<=n;j++)
            putchar(ans[i][j]);
        printf("\n");
