						judge[i - 2][j - 2] = judge[i - 3][j - 3] =
						judge[i - 4][j - 4] = judge[i - 5][j - 5] =
						judge[i - 6][j - 6] = true;
					else if (map[i - p][j - p] != word[p])
						break;
				}
			//up
			if (i - 6 >= 0)
				for (int p = 0; p <= 7; p++) {
					if (p == 7)
						judge[i][j] = judge[i - 1][j] =
						judge[i - 2][j] = judge[i - 3][j] =
						judge[i - 4][j] = judge[i - 5][j] =
						judge[i - 6][j] = true;
					else if (map[i - p][j] != word[p])
						break;
				}
			//right_up
			if (i - 6 >= 0 && j + 6 <= n - 1)
				for (int p = 0; p <= 7; p++) {
					if (p == 7)
						judge[i][j] = judge[i - 1][j + 1] =
						judge[i - 2][j + 2] = judge[i - 3][j + 3] =
						judge[i - 4][j + 4] = judge[i - 5][j + 5] =
						judge[i - 6][j + 6] = true;
					else if (map[i - p][j + p] != word[p])
						break;
				}
			//right
			if (j + 6 <= n - 1)
				for (int p = 0; p <= 7; p++) {
					if (p == 7)
						judge[i][j] = judge[i][j + 1] =
						judge[i][j + 2] = judge[i][j + 3] =
						judge[i][j + 4] = judge[i][j + 5] =
						judge[i][j + 6] = true;
					else if (map[i][j + p] != word[p])
						break;
				}
			//right_down
			if (i + 6 <= n - 1 && j + 6 <= n - 1)
				for (int p = 0; p <= 7; p++) {
					if (p == 7)
						judge[i][j] = judge[i + 1][j + 1] =
						judge[i + 2][j + 2] = judge[i + 3][j + 3] =
						judge[i + 4][j + 4] = judge[i + 5][j + 5] =
						judge[i + 6][j + 6] = true;
					else if (map[i + p][j + p] != word[p])
						break;
				}
			//down
			if (i + 6 <= n - 1)
				for (int p = 0; p <= 7; p++) {
					if (p == 7)
						judge[i][j] = judge[i + 1][j] =
						judge[i + 2][j] = judge[i + 3][j] =
						judge[i + 4][j] = judge[i + 5][j] =
						judge[i + 6][j] = true;
					else if (map[i + p][j] != word[p])
						break;
				}
			//left_down
			if (i + 6 <= n - 1 && j - 6 >= 0)
				for (int p = 0; p <= 7; p++) {
					if (p == 7)
						judge[i][j] = judge[i + 1][j - 1] =
						judge[i + 2][j - 2] = judge[i + 3][j - 3] =
						judge[i + 4][j - 4] = judge[i + 5][j - 5] =
						judge[i + 6][j - 6] = true;
					else if (map[i + p][j - p] != word[p])
						break;
				}
			//left
			if(j-6>=0)
				for (int p = 0; p <= 7; p++) {
					if (p == 7)
						judge[i][j] = judge[i][j - 1] =
						judge[i][j - 2] = judge[i][j - 3] =
						judge[i][j - 4] = judge[i][j - 5] =
						judge[i][j - 6] = true;
					else if (map[i][j - p] != word[p])
						break;
				}
			//ending    真tmd不容易
		}
	for (int i = 0; i < n; i++) {
		for (int j = 0; j < n; j++) {
			if (judge[i][j])
				cout << map[i][j];
			else
				cout << "*";
		}
		cout << endl;
	}
	//infile.close();
	return 0;
}
