            sum:=0;
            if a[i,j]='y' then search(i,j,'yx');//右下方
            end;
        for i:=1 to n do
        begin
            for j:=1 to n do
                if b[i,j] then write(a[i,j])
                    else write('*');
                        writeln;
        end;
    end.
