bool GO(int x, int y, int k)
{
    int tx = mx[k], ty = my[k];
    if(x+6*tx < 1 || x+6*tx > n || y+6*ty < 1 || y+6*ty > n)
        return false;
    for(int i = 0; i < 7; i++)
        if(g[x+i*tx][y+i*ty]-yz[i])
            return false;
    for(int i = 0; i < 7; i++)
        book[x+i*tx][y+i*ty] = true;
    return true;
}
void Find(int x, int y)
{
    int dir = -1;
    for(int i = 0; i < 8; i++)
        if(g[x+mx[i]][y+my[i]] == 'i')
            GO(x,y,i);
    return;
}
int main()
{
    scanf("%d",&n);
    for(int i = 1; i <= n; i++)
        scanf("%s",g[i]+1);
    for(int i = 1; i <= n; i++)
