```cpp
#include<cstdio>
#include<cstdlib>
using namespace std;
char grid[101][101];
bool used[101][101];//标记数组 
int N;
bool dfs(char a,int dir1,int dir2,int pos1,int pos2)//无脑深搜 
{
    //检查是否是连续的“yizhong” 
    if(a=='i')
    {
        if(grid[pos1+dir1][pos2+dir2]!='z')
        {
            return false;
        }
    }
    else if(a=='z')
    {
        if(grid[pos1+dir1][pos2+dir2]!='h')
        {
            return false;
        }
    }
    else if(a=='h')
    {
        if(grid[pos1+dir1][pos2+dir2]!='o')
        {
            return false;
        }
    }
    else if(a=='o')
    {
        if(grid[pos1+dir1][pos2+dir2]!='n')
        {
            return false;
        }
    }
    else if(a=='n')
    {
        if(grid[pos1+dir1][pos2+dir2]!='g')
        {
            return false;
        }
    }
    else if(a=='g')
    {
        return true;
    }
    dfs(grid[pos1+dir1][pos2+dir2],dir1,dir2,pos1+dir1,pos2+dir2);
}
void read()//输入数据 
{
    scanf("%d",&N);
    for(int i=0;i<N;i++)
    {
        scanf("%s",grid[i]);
    }
    for(int i=0;i<N;i++)
    {
        for(int j=0;i<N;i++)
        {
            used[i][j]=false;
        }
    }
}
void solve()
{
    read();
    for(int i=0;i<N;i++)
    {
        for(int j=0;j<N;j++)
        {
            //找到一个'y' 
            if(grid[i][j]=='y')
            {
                for(int x=-1;x<=1;x++)
                {
                    for(int y=-1;y<=1;y++)
                    {
                        //找到'i',确定“yizhong”的方向 
                        if(grid[i+x][j+y]=='i')
                        {
                            if(dfs('i',x,y,i+x,j+y)==true)
                            {
                                //返回做标记 ，兼容重合与不重合的情况。 
                                for(int temp=0;temp<=6;temp++)
                                {
                                    if(x==0&&y<0)
                                    {
                                        used[i][j-temp]=true;
                                    }
                                    else if(x==0&&y>0)
                                    {
                                        used[i][j+temp]=true;
                                    }
                                    else if(x<0&&y<0)
                                    {
                                        used[i-temp][j-temp]=true;
                                    }
                                    else if(x<0&&y==0)
                                    {
                                        used[i-temp][j]=true;
                                    }
                                    else if(x<0&&y>0)
                                    {
                                        used[i-temp][j+temp]=true;
                                    }
                                    else if(x>0&&y<0)
                                    {
                                        used[i+temp][j-temp]=true;
                                    }
                                    else if(x>0&&y==0)
                                    {
                                        used[i+temp][j]=true;
                                    }
                                    else if(x>0&&y>0)
                                    {
                                        used[i+temp][j+temp]=true;
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }
    }
    //打印，标为“true”的地方打印原字符，“false”的地方打’*‘ 
    for(int i=0;i<N;i++)
    {
        for(int j=0;j<N;j++)
        {
            if(used[i][j]==true)
            {
                printf("%c",grid[i][j]);
            }
            else
            {
                printf("*");
            }
        }
        printf("\n");
    }
}
int main()
{
    solve();
}
