           then if a[x+3,y-3]='h'
                  then if a[x+4,y-4]='o'
                         then if a[x+5,y-5]='n'
                                then if a[x+6,y-6]='g'
                                       then for ii:=6 downto 0 do
                                              f[x+ii,y-ii]:=true;
end;
procedure judge7(x,y:longint);
begin
  if a[x,y-1]='i'
    then if a[x,y-2]='z'
           then if a[x,y-3]='h'
                  then if a[x,y-4]='o'
                         then if a[x,y-5]='n'
                                then if a[x,y-6]='g'
                                       then for jj:=6 downto 0 do
                                              f[x,y-jj]:=true;
end;
procedure judge8(x,y:longint);
begin
  if a[x-1,y-1]='i'
    then if a[x-2,y-2]='z'
           then if a[x-3,y-3]='h'
                  then if a[x-4,y-4]='o'
                         then if a[x-5,y-5]='n'
                                then if a[x-6,y-6]='g'
                                       then for ii:=6 downto 0 do
                                              f[x-ii,y-ii]:=true;
end;
procedure judge(x,y:longint);            //判断总共8个方向的情况
begin
  judge1(x,y);
  judge2(x,y);
  judge3(x,y);
  judge4(x,y);
  judge5(x,y);
  judge6(x,y);
  judge7(x,y);
  judge8(x,y);
end;


begin
  fillchar(a,sizeof(a),'b');
  fillchar(f,sizeof(f),false);
  readln(n);
  for i:=1 to n do
    begin
      for j:=1 to n do
        read(a[i,j]);
      readln;
    end;                                   //读入
  for i:=1 to n do
    for j:=1 to n do
      if a[i,j]='y'
        then judge(i,j);                   //判断
  for i:=1 to n do
    begin
      for j:=1 to n do
        if f[i,j]
          then write(a[i,j])
          else write('*');                 //输出
      writeln;
    end;
end.
```
该程序没有考虑交叉的情况，因为题目中明确地说不会出现这样的情况，这个程序对于这样的情况会把交叉的两者都输出。

这道题常量数组可以存储方向和需要的单词

```cpp

const
need:array[1..7]of char='yizhong';
x:array[1..8]of -1..1=(-1,-1,-1,0,0,1,1,1);
y:array[1..8]of -1..1=(-1,0,1,-1,1,-1,0,1);

var
 n,i,j:longint;
 a:array[1..100,1..100]of char;
 b:array[1..100,1..100]of boolean; //是否显示

function can(x:longint):boolean; //是否在方阵范围内
begin
 if (x>0)and(x<=n) then exit(true);
 exit(false);
end;

procedure try(kx,ky:longint);
var
 i,j:longint;
 ok:boolean;
begin
 for i:=1 to 8 do //八个方向
 if can(kx+x[i]*6) and can(ky+y[i]*6) then
 begin
  ok:=true;
  for j:=0 to 6 do //逐个比较
  if a[kx+x[i]*j,ky+y[i]*j]<>need[j+1] then
  begin
   ok:=false;
   break;
  end;
  if ok then
  for j:=0 to 6 do
   b[kx+x[i]*j,ky+y[i]*j]:=true;
 end;
end;

begin
 readln(n);
 for i:=1 to n do
 begin
  for j:=1 to n do
   read(a[i,j]);
  readln;
 end;
 for i:=1 to n do
  for j:=1 to n do
  if not b[i,j] then
    try(i,j);
 for i:=1 to n do
 begin
  for j:=1 to n do
   if b[i,j] then write(a[i,j])
   else write('*');
  writeln;
 end;
end.

