			if (edge[i].v == v) return edge[i].w;
		edge[++cnt] = (node) {head[v%SIZE],v,0};
		head[v%SIZE] = cnt;
		return edge[cnt].w;
	}
} Hash;

int main() {
	n = read(); c = read();
	for (register int i = 1; i <= n; ++i) {
		++Hash[a[i] = read()];
		a[i] -= c;
	}
	for (register int i = 1; i <= n; ++i)
		ans += Hash[a[i]];
	printf("%lld\n", ans);
} 
