long long s=0;
for (i=1;i<=n;i++) 
  {
  int pos1=lower_bound(a+i,a+n+1,a[i]+c)-a;//查找数组中第一个大于等于a[i]+c的数字的位置
  int pos2=upper_bound(a+i,a+n+1,a[i]+c)-a;//查找数组中第一个大于a[i]+c的数字的位置
  s+=pos2-pos1;//自己把数组列出来之后就可以知道pos2-pos1就是a[i]+c在数组中的个数。
  }
printf("%lld",s);
}
