        map[x][y]=z;
    }
}
int max(int a,int b,int c,int d)
{
    int ans=a;
    if(b>ans)ans=b;//薛山定律来保存
    if(c>ans)ans=c;
    if(d>ans)ans=d;
    return ans;
}
