    else a[x1][y1][x2][y2]=map[x1][y1]+map[x2][y2]+max(a[x1-1][y1][x2-1][y2],a[x1-1][y1][x2][y2-1],a[x1][y1-1][x2-1][y2],a[x1][y1-1][x2][y2-1]);
