	sort(a+1,a+n+1,cmp);
	memset(f,-1,sizeof(f));
	int ans=19260817;
	for(int i=1;i<=k+1;i++){
		ans=min(ans,dfs(i,i-1,1));//第几本 已经删了几本 选了几本留下
	}
	printf("%d",ans);
	return 0;
}
