	while(c>='0'&&c<='9') x=(x<<3)+(x<<1)+c-'0',c=getchar();
	return x;
}
void qsort(int l1,int r1){
	int l,r,mid;
	l=l1;
	r=r1;
	mid=(h[l]+h[r])/2;
	while(l<=r){
		while(h[l]<mid) l++;
		while(h[r]>mid) r--;
		if(l<=r){
			swap(h[l],h[r]);
			swap(w[l],w[r]);
			l++;
			r--;
		}
	}
	if(l1<r) qsort(l1,r);
	if(l<r1) qsort(l,r1);
}
int ho(int a){
	return a>=0?a:-a;
}
int mi(int x,int y){
	return x<y?x:y;
}
