	for(int i=1;i<=n;i++)
	  for(int j=1;j<=k;j++) f[i][j]=inf;//初始化表格
	for(int i=1;i<=n;i++) f[i][1]=0;//从前i本中取一本不管怎样差值都是0
	for(int i=2;i<=n;i++)//i，j从二开始枚举
