	    for(int l=1;l<i;l++){//枚举前一个数
	    	int minus=ho(w[i]-w[l]);
	    	f[i][j]=mi(f[i][j],f[l][j-1]+minus);
	    }
	int ans=inf;
	for(int i=1;i<=n;i++) ans=mi(ans,f[i][k]);//因为最小值不一定要取最后一个
    //所以要在最后一行扫一遍而不是直接f[n][k]
	cout<<ans;
}
