#include <string>
#include <cstdio>
#include <cstring>
#include <iostream>
#include <algorithm>
using namespace std;

const int N = 105;
const int INF = 1e9;

int n, k;
int h[N], w[N];
int a[N], b[N];
int f[N][N]; // f[i][j]记录前i本书中保留j本, 相邻宽度差之和的最小值

int cmp(int x, int y) { return h[x] < h[y]; }

int abs(int x) { return x > 0 ? x : -x; }

int work()
{
	int m = n - k;

	for(int i = 1; i <= n; ++ i)
		for(int j = 1; j <= m; ++ j)
			f[i][j] = INF;

	for(int i = 1; i <= n; ++ i) f[i][1] = 0; // 初值

	for(int i = 2; i <= n; ++ i)
		for(int j = 2; j <= m; ++ j)
			for(int l = 1; l < i; ++ l) // 枚举前一本书的位置
				f[i][j] = min(f[i][j], f[l][j-1] + abs(a[i] - a[l]));

	int ans = INF;
	for(int i = 1; i <= n; ++ i) ans = min(ans, f[i][m]);

	return ans;
}

int main()
{

	cin >> n >> k;
	for(int i = 1; i <= n; ++ i) cin >> h[i] >> w[i], b[i] = i;

	sort(b + 1, b + n + 1, cmp); // 按照高度排序

	for(int i = 1; i <= n; ++ i) a[i] = w[b[i]]; // 按照高度顺序记录宽度

	cout << work() << endl;

	return 0;
}
