    for(i=1;i<=p-k;i++)
    {
        min=10;
              for(j=1;j<=p-s+1;j++)//p-s+1，p-(s-1)的化简，求出可以循环的边界，避免到最后无数可选
        {
            if(a[j]<min){min=a[j]; t=j;}//求最小。可以看出t在改变，所以后面要赋值给t2
        }
        s--;
        b[i]=a[t];
        t2=t+1;
        f++;
    }
    i=1;
    int flag=0;
    for(i=1;i<=f-1;i++) 
    {
        if(f-1>1&&flag==0&&b[i]==0) continue;//去前导0，只要flag为假，就说明此时的0是前导0，不输出。f-1>1为了过最后的数据
       else
