        while(q.top().second+len-j>=s.size() || vis[q.top().second]==1)
        {
            if(vis[q.top().second]==0)qq.push(q.top());
            q.pop();
        }
        ans+=q.top().first;
        for(int i=0;i<=q.top().second;i++)vis[i]=1;
        q.pop();
        while(!qq.empty())
        {
            q.push(qq.front());
            qq.pop();
        }
    }
    int kkk=delete0(ans); //别忘了，删除前导0
    for(int i=kkk;i<ans.size();i++)cout<<ans[i];
    return 0;
}
