```cpp
var
  n,m,i,j,k,p,max,ans:longint;
  a,f,t:array[0..100000] of longint;
begin
  readln(n);
  max:=1;
  for i:=1 to n do
  begin
     read(a[i]);
     f[i]:=1;
  end;
  for i:=1 to n do
  begin
     for j:=1 to i-1 do
       if (a[j]>a[i]) and (f[j]+1>f[i]) then
       begin
         f[i]:=f[j]+1;
         if f[i]>max then
           max:=f[i];
       end;
       if f[i]=1 then t[i]:=1;
     for j:=1 to i-1 do
       if (a[j]>a[i]) and (f[j]+1=f[i]) then//数量转移方程
          t[i]:=t[i]+t[j] else
          if (a[j]=a[i]) and (f[i]=f[j]) then t[j]:=0;//别忘了这里。。。去重，否则的话（嘿嘿嘿）
  end;
  write(max,' ');
  for i:=1 to n do
    if f[i]=max then
      ans:=ans+t[i];
    writeln(ans);//全加起来！！！！
end.
