    for (int i=1; i<=n; i++)
    {
        scanf("%d",&a[i]);
        f[i]=1;
        for (int j=1; j<i; j++)
            if (a[j]>a[i]) f[i]=max(f[i],f[j]+1);
        if (f[i]==1) t[i]=1;
