//此题为lis的计重求方案数题，lis简单，计重难

//首先若不计重 ，则将代码中有关chong[]的东西去掉，那么g[]继承前一状态的方案数

//这样 像6 6 6 6 6 这样的数据会输出1 5 而答案为1 1

//其实如果i<j且a[i]==a[j],则j的f与g是优于或等于i的，转移时不考虑i(值标记“chong”来实现)，

//只考虑j；可将第二重循环倒过来实现这一点

```cpp
//最后统计时道理同上 
#include<iostream>
#include<cstdio>
#include<cstring>
#include<algorithm>
using namespace std;
const int N=5050,M=50000;//其实数据大小我也不知道，瞎猜一个M 
int a[N],f[N],g[N];
bool chong[M]={false};
int n;
int main() 
{
      scanf("%d",&n);
      fill(f,f+N,1);
      fill(g,g+N,1);
      for(int i=1;i<=n;i++)scanf("%d",&a[i]);
      for(int i=2;i<=n;i++){
          memset(chong,0,sizeof(chong));
          for(int j=i-1;j>=1;j--)
              if(a[i]<a[j]&&chong[a[j]]==false){
                  if(f[i]==f[j]+1)g[i]+=g[j];//与当前等长的序列g累加 
                  if(f[i]<f[j]+1){
                    f[i]=f[j]+1;
                    g[i]=g[j];//有更长的序列，g则更新 
                  }
                  chong[a[j]]=true;
              }
      }    
      int maxn=0,tot=0;//max为fmax ；tot为对应的g 
      memset(chong,0,sizeof(chong));
      for(int i=n;i>=1;i--)
      {
           if(chong[a[i]])continue;
           if(f[i]>maxn){
             maxn=f[i];
             tot=g[i];
          }
          else if(f[i]==maxn)
             tot+=g[i];
          chong[a[i]]=true;
      }
      cout<<maxn<<" "<<tot<<endl;
      return 0;
}
