    for(int i=1;i<=n;i++)read(a[i]);
    int A=a[2]-a[1],C=a[2]%a[1]; //建立两个基准数，树为标准
    for(int i=3;i<=n;i++){
        if(a[i]-a[i-1]!=A)f1=0;
        if(a[i]*a[1]!=a[i-1]*a[2])f2=0; /*等比要变一下，不能直接简单相除，细节处理*/
        if(a[i]%a[i-1]!=C)f3=0;
    }
    if(f1||f2||f3)printf("YES\n");
    else printf("NO\n");
    return 0;
}
