#include<bits/stdc++.h>
using namespace std;
int m[10000005];//存重数 
int a,b,c,d,e;
int main()
{
	cin>>a>>b>>c>>d>>e;
	for(int i=a;i<=b;i++)//在i进制下 
	{
		for(int j=0;j<i;j++)//枚举第一个数 
		{
			for(int k=1;k<i;k++)//枚举第二个数 
			{
				if(j!=k)//两数不能一样 
				{
					int v1=0;//构造用 
					int v2=0;//计数 用 
					while(v1<=d)
					{
						//循环用两个数构造 
						if(v2%2==0)
						{
							v1=v1*i+j;
						}
						else
						{
							v1=v1*i+k;
						} 
						v2++;
						if(v1>=c && v1<=d)
						{
							m[v1]++;//如果是波浪数，存到对应位置 
						}
					}
				}
			}	
		} 
	}
	for(int i=c;i<=d;i++)
	{
		if(m[i]==e)//如果有题目要求的重数，输出
		{
			cout<<i<<endl;	
		} 
	}
	return 0;
}
```
