#include<bits/stdc++.h>
#define re register
#define read(x)\
{\
    re int f=1;\
    x=0;\
    re char c=getchar();\
    while(!(c>='0'&&c<='9'))\
    {\
        if(c=='-')\
        f=-1;\
        c=getchar();\
    }\
    while((c>='0'&&c<='9'))\
    {\
        x=x*10+(c-'0');\
        c=getchar();\
    }\
    x=x*f;\
}
#define max_(x,q)\
    (x<q)?q:x
using namespace std;
int ans,n,a[1000001],l[1000001],r[1000001],g,b,t=n;
int main()
{
    read(n);
    t=n;
    for(re int i=1;i<=n;i++)
    {
        read(a[i]);
        if(a[i]==1&&b==0)
        b=1;
        if(a[i]==0&&g==0)
        g=1;
    }
    if(g==0||b==0)
    {
        putchar('0');
        exit(0);
    }
    for(int i=1;i<=n;i++)
    {
        if(a[i]==1)
        t--;
        else
        t++;
        if(!l[t]&&t!=n)
        l[t]=i;
        else
        r[t]=i;
    }
    for(int i=0;i<=2*n;i++)
    ans=max_(ans,r[i]-l[i]);
